mapper.update:
UPDATE vendor_api_keys
SET email_pk   = #{emailPk},
    secret_key = #{secretKey},
    status     = #{status},
    vendor     = #{vendor}
WHERE id = #{id}

mapper.select:
SELECT id, email_pk, access_key, secret_key, status, vendor, created_dt
FROM vendor_api_keys
WHERE id = #{id}

SELECT id, email_pk, access_key, secret_key, status, vendor, created_dt
FROM vendor_api_keys
WHERE access_key = #{accessKey}
